/*Table structure for table `content_type` */

DROP TABLE IF EXISTS `content_type`;

CREATE TABLE `content_type` (
  `c_type_id` bigint(100) NOT NULL AUTO_INCREMENT,
  `t_id` bigint(100) DEFAULT NULL COMMENT '类别Id',
  `c_id` bigint(100) DEFAULT NULL COMMENT '小说Id',
  PRIMARY KEY (`c_type_id`)
) ENGINE=InnoDB AUTO_INCREMENT=26 DEFAULT CHARSET=utf8;

/*Data for the table `content_type` */

insert  into `content_type`(`c_type_id`,`t_id`,`c_id`) values (7,1,1),(8,2,1),(9,3,1),(10,4,1),(11,6,1),(12,5,10),(13,3,11),(14,5,11),(15,1,12),(16,2,13),(17,6,13),(18,1,9),(19,4,8),(20,6,7),(21,4,5),(22,6,4),(23,6,3);

/*Table structure for table `country` */

DROP TABLE IF EXISTS `country`;

CREATE TABLE `country` (
  `Id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `countryname` varchar(255) DEFAULT NULL COMMENT '名称',
  `countrycode` varchar(255) DEFAULT NULL COMMENT '代码',
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB AUTO_INCREMENT=185 DEFAULT CHARSET=utf8 COMMENT='国家信息';

